import React from 'react'
import logo from './img/logo_talento.png';
import { useLocation, useNavigate } from 'react-router-dom';
const EditModule = () => {

  <Logo />
  const location = useLocation();
  const navigate = useNavigate();
  const { title } = location.state || { title: 'Default Title' };
  const [newTitle, setNewTitle] = React.useState(title);

  const handleSave = () => {
    // Aquí puedes manejar la lógica de guardado del nuevo título
    console.log('Nuevo título guardado:', newTitle);
    navigate('/');
  };

  return (
    <div className="flex flex-col items-center w-full p-4">
      <div className="bg-[#082563] rounded-lg shadow-lg p-2 mb-2 w-[calc(100%)]">
        <div className="flex items-center pl-4">
          <img src="/path/to/logo" alt="Logo Talento" />
        </div>
      </div>

      <h1 className="text-3xl font-bold mb-4">Editar Módulo</h1>
      <div className="w-full max-w-4xl bg-white rounded-lg shadow-lg p-4 mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Nombre del Módulo:
        </label>
        <input
          type="text"
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500"
        />
        <button onClick={handleSave} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
          Guardar
        </button>
      </div>
    </div>
  );
};

export default EditModule;
