import React from 'react';
import Logo from '../img/logo_talento.png';
import { useLocation, useNavigate } from 'react-router-dom';

const EditModule = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { id, title, status, fecha_inicio, fecha_fin } = location.state || { id: null, title: '', status: '', fecha_inicio: '', fecha_fin: '' };



  const formatDate = (dateString) => {
    const dateObject = new Date(dateString);
    return dateObject.toLocaleDateString('es-ES'); 
  };

  const [moduleID, setModuleID] = React.useState(id)
  const [moduleTitle, setModuleTitle] = React.useState(title);
  const [moduleStatus, setModuleStatus] = React.useState(status);
  const [moduleStartDate, setModuleStartDate] = React.useState(formatDate(fecha_inicio));
  const [moduleEndDate, setModuleEndDate] = React.useState(formatDate(fecha_fin));

  const handleSave = async () => {
    const formData = {
      id: moduleID,
      nombre_modulo: moduleTitle,
      fecha_inicio: moduleStartDate,
      fecha_fin: moduleEndDate,
      estado: moduleStatus,
    };

    try {

       
        await fetch(`/api/modulo/${moduleID}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
      

      console.log('Datos del módulo guardados:', formData);
      navigate('/home');
    } catch (error) {
      console.error('Error al guardar el módulo:', error);

    }
  };
  return (
    <div className="flex flex-col items-center w-full p-4">
      <div className="bg-[#082563] rounded-lg shadow-lg p-2 mb-2 w-[calc(100%)]">
        <div className="flex items-center pl-4">
          <img src={Logo} alt="Logo Talento" />
        </div>
      </div>

      <h1 className="text-3xl font-bold mb-4">Editar Módulo</h1>
      <div className="w-full max-w-4xl bg-white rounded-lg shadow-lg p-4 mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Nombre del Módulo:
        </label>
        <input
          type="text"
          value={moduleTitle}
          onChange={(e) => setModuleTitle(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Fecha de Inicio:
        </label>
        <input
          type="text"
          value={moduleStartDate}
          onChange={(e) => setModuleStartDate(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Fecha de Fin:
        </label>
        <input
          type="text"
          value={moduleEndDate}
          onChange={(e) => setModuleEndDate(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Estado del Módulo:
        </label>
        <select
          value={moduleStatus}
          onChange={(e) => setModuleStatus(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        >
          <option value="Finalizado">Finalizado</option>
          <option value="En curso">En curso</option>
          <option value="Próximamente">Próximamente</option>
        </select>
        
        <button
          onClick={handleSave}
          className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300"
        >
          Guardar
        </button>
      </div>
    </div>
  );
};

export default EditModule;
