import React, { useEffect, useState } from 'react';
import Logo from '../../img/logo_talento.png';
import axios from 'axios';

export default function ListaNotas() {
    const [data, setData] = useState([]);
    const [editingData, setEditingData] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/api/modulos/nombres')
          .then(response => {
            setData(response.data);
            const initialEditingData = response.data.map(entry => ({
              estudiante: entry.estudiante,
              modulos: entry.modulos.map(modulo => ({
                nombre: modulo.nombre,
                promedio: modulo.promedio
              }))
            }));
            setEditingData(initialEditingData);
          })
          .catch(error => {
            console.error('Error fetching data:', error);
          });
      }, []);

      const handleInputChange = (estudianteIndex, moduloIndex, field, value) => {
        const updatedData = [...editingData];
        updatedData[estudianteIndex].modulos[moduloIndex][field] = value;
        setEditingData(updatedData);
      };


    const moduleNames = data.reduce((acc, entry) => {
    entry.modulos.forEach(modulo => {
      if (!acc.includes(modulo.nombre)) {
        acc.push(modulo.nombre);
      }
    });
    return acc;
  }, []);


    return (
        <div className="flex w-full flex-col">
          <div className="bg-[#082563] rounded-lg shadow-lg p-2 mb-2 w-[calc(100%)]">
            <div className="flex items-center pl-4">
              <img src={Logo} alt="Logo Talento" />
            </div>
          </div>
          <div className="flex flex-col p-4">
        <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold">Lista de Notas</h2>
        <table className="table-auto w-full border-collapse border border-gray-200 mt-4">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-200 px-4 py-2">Estudiante</th>
              {moduleNames.map((name, index) => (
                <th key={index} className="border border-gray-200 px-4 py-2">{name}</th>
              ))}
              <th className="border border-gray-200 px-4 py-2">Promedio</th>
            </tr>
          </thead>
          <tbody>
            {editingData.map((student, estudianteIndex) => (
              <tr key={estudianteIndex} className="bg-white border-b">
                <td className="border border-gray-200 px-4 py-2">{student.estudiante}</td>
                {moduleNames.map((name, moduloIndex) => {
                  const modulo = student.modulos.find(mod => mod.nombre === name);
                  return (
                    <td key={moduloIndex} className="border border-gray-200 px-4 py-2">
                      <input
                        type="text"
                        className="border border-gray-300 rounded px-2 py-1 w-full"
                        value={modulo ? modulo.promedio : ''}
                        onChange={(e) => handleInputChange(estudianteIndex, moduloIndex, 'promedio', e.target.value)}
                      />
                    </td>
                  );
                })}
                <td className="border border-gray-200 px-4 py-2">
                  {/* Aquí puedes calcular el promedio si lo deseas */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
