import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Logo from '../../img/logo_talento.png';

export default function Sesion() {
  const { id_modulo } = useParams();
  const [sesiones, setSesiones] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:3001/api/sesiones/${id_modulo}`)
      .then(response => {
        setSesiones(response.data);
        console.log(response.data)
      })
      .catch(error => {
        console.error('Error fetching sesiones:', error);
      });
  }, [id_modulo]);

  return (
    <div className="flex w-full flex-col">
      <div className="bg-[#082563] rounded-lg shadow-lg p-2 mb-2 w-[calc(100%)]">
        <div className="flex items-center pl-4">
          <img src={Logo} alt="Logo Talento" />
        </div>
      </div>
      <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold mb-4">Sesiones del MÃ³dulo</h2>
      <div className="flex flex-col">
        {sesiones.map((sesion, index) => (
          <div key={index} className="bg-white border border-gray-300 rounded-lg shadow-lg p-4 mb-4">
            <h3 className="text-lg sm:text-xl md:text-2xl">{sesion.nombre_sesion}</h3>
            <p className="text-sm sm:text-base">Estado: {sesion.estado}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
