import React from 'react'
import Logo from '../img/logo_talento.png';
import { useLocation, useNavigate } from 'react-router-dom';
const EditModule = () => {

  const location = useLocation();
  const navigate = useNavigate();
  const { title, date, status, progress } = location.state || { title: '', date: '', status: '', progress: 0 };
  const [moduleTitle, setModuleTitle] = React.useState(title);
  const [moduleDate, setModuleDate] = React.useState(date);
  const [moduleStatus, setModuleStatus] = React.useState(status);
  const [moduleProgress, setModuleProgress] = React.useState(progress);

  const handleSave = () => {
    if (moduleProgress < 0 || moduleProgress > 100) {
      alert("El progreso debe estar entre 0 y 100.");
      return;
    }
    console.log('Datos del módulo guardados:', { moduleTitle, moduleDate, moduleStatus, moduleProgress });
    navigate('/');
  };

  return (
    <div className="flex flex-col items-center w-full p-4">
      <div className="bg-[#082563] rounded-lg shadow-lg p-2 mb-2 w-[calc(100%)]">
        <div className="flex items-center pl-4">
          <img src={Logo} alt="Logo Talento" />
        </div>
      </div>

      <h1 className="text-3xl font-bold mb-4">Editar Módulo</h1>
      <div className="w-full max-w-4xl bg-white rounded-lg shadow-lg p-4 mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Nombre del Módulo:
        </label>
        <input
          type="text"
          value={moduleTitle}
          onChange={(e) => setModuleTitle(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Fecha del Módulo:
        </label>
        <input
          type="text"
          value={moduleDate}
          onChange={(e) => setModuleDate(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Estado del Módulo:
        </label>
        <select
          value={moduleStatus}
          onChange={(e) => setModuleStatus(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        >
          <option value="Finalizado">Finalizado</option>
          <option value="En curso">En curso</option>
          <option value="Próximamente">Próximamente</option>
        </select>
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Progreso del Módulo:
        </label>
        <input
          type="number"
          value={moduleProgress}
          onChange={(e) => setModuleProgress(e.target.value)}
          min="0"
          max="100"
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <button onClick={handleSave} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
          Guardar
        </button>
      </div>
    </div>
  );
};

export default EditModule;
