import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const NewModule = () => {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [status, setStatus] = useState('Próximamente');
  const [progress, setProgress] = useState(0);
  const navigate = useNavigate();

  const handleSave = () => {
    if (progress < 0 || progress > 100) {
      alert("El progreso debe estar entre 0 y 100.");
      return;
    }

    // Lógica para guardar el nuevo módulo
    console.log('Nuevo módulo creado:', { title, date, status, progress });
    navigate('/');
  };

  return (
    <div className="flex flex-col items-center w-full p-4">
      <div className="bg-[#082563] rounded-lg shadow-lg p-2 mb-2 w-[calc(100%)]">
        <div className="flex items-center pl-4">
          <img src="/path/to/logo" alt="Logo Talento" />
        </div>
      </div>
      <h1 className="text-3xl font-bold mb-4">Crear Nuevo Módulo</h1>
      <div className="w-full max-w-4xl bg-white rounded-lg shadow-lg p-4 mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Nombre del Módulo:
        </label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Fecha del Módulo:
        </label>
        <input
          type="text"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Estado del Módulo:
        </label>
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        >
          <option value="Finalizado">Finalizado</option>
          <option value="En curso">En curso</option>
          <option value="Próximamente">Próximamente</option>
        </select>
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Progreso del Módulo:
        </label>
        <input
          type="number"
          value={progress}
          onChange={(e) => setProgress(e.target.value)}
          min="0"
          max="100"
          className="w-full px-4 py-2 border rounded-lg text-gray-700 focus:outline-none focus:border-blue-500 mb-4"
        />
        <button onClick={handleSave} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
          Guardar
        </button>
      </div>
    </div>
  );
};

export default NewModule;
