import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function EditarNotas() {
    const { id_usuario } = useParams();
    const [usuario, setUsuario] = useState(null);
    const [notas, setNotas] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch user data and notes
        axios.get(`http://localhost:3001/api/notas/${id_usuario}`)
          .then(response => {
            setNotas(response.data);
            if (response.data.length > 0) {
              setUsuario(response.data[0].estudiante); // Assuming the student's name is in the 'estudiante' field
            }
            setLoading(false);
          })
          .catch(error => {
            console.error('Error fetching data:', error);
            setLoading(false);
          });
      }, [id_usuario]);

  const handleInputChange = (modulo, nota) => {
    setNotas(prevNotas => prevNotas.map(
      n => n.modulo === modulo ? { ...n, nota } : n
    ));
  };

  const handleSave = () => {
    
    console.log('Saving notes:', notas);

  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!usuario) {
    return <div>No se encontr√≥ el usuario.</div>;
  }

  return (
    <div className="flex flex-col items-center p-4">
      <h2 className="text-2xl font-bold mb-4">Editar Notas de {usuario}</h2>
      {notas.map((nota, index) => (
        <div key={index} className="flex flex-col mb-4">
          <label className="mb-2">{nota.modulo}</label>
          <input
            type="number"
            value={nota.nota}
            onChange={(e) => handleInputChange(nota.modulo, e.target.value)}
            className="p-2 border border-gray-300 rounded"
          />
        </div>
      ))}
      <button onClick={handleSave} className="bg-[#072563] text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors duration-300">
        Guardar
      </button>
    </div>
  );
}
